const curPage=window.location.pathname,Swal=window["Swal"];function showMessage(e,t,o){Swal.fire({title:e,text:t,icon:o,timer:1500,timerProgressBar:!0,showConfirmButton:!1})}switch(curPage){case"/index.html":const d=document.querySelectorAll(".link-index");d.forEach(e=>{e.classList.add("uk-active")});break;case"/":const e=document.querySelectorAll(".link-index");e.forEach(e=>{e.classList.add("uk-active")});break;case"/contact.html":const f=document.querySelectorAll(".link-contact");f.forEach(e=>{e.classList.add("uk-active")});break;case"/winkelmandje.html":const g=document.querySelectorAll(".link-winkelmandje");g.forEach(e=>{e.classList.add("uk-active")});break;case"/login.html":const h=document.querySelectorAll(".link-user");h.forEach(e=>{e.style.color="black"})}if(sessionStorage.getItem("live_klant")){const n=document.querySelectorAll(".link-user"),o=(n.forEach(e=>{e.style.color="#0F7AE5"}),JSON.parse(sessionStorage.getItem("live_klant")));if(o.admin){const r=document.querySelectorAll(".link-admin");r.forEach(e=>{e.style.color="red"})}const p=document.querySelectorAll('[href="login.html"]');p.forEach(e=>{e.innerText="Log uit",e.style.color="red",e.addEventListener("click",e=>{sessionStorage.removeItem("live_klant"),showMessage("Je bent uitgelogd","Tot ziens!","success")})})}